template.value:
if( have_rows('$NAME$','$PARA$') ):
    while ( have_rows('$NAME$','$PARA$') ) : the_row();
       echo "$String$";
    endwhile;
endif;
wp_reset_postdata();

$name$ = get_field('$fname$',$param$);
if( !empty($name$) ): ?>
    <img src="<?php echo $name$['url']; ?>" alt="<?php echo $name$['alt']; ?>" />
<?php endif;

$favicon=get_field('favicon','option');
    if (!empty($favicon)):
        echo "<link rel=\"icon\" type=\"image/x-icon\" href=\"$favicon\">";
    endif;

/*
* Template Name: $TEMNAME$
*
* */

 get_header(); 

 while(have_posts()):the_post();
    the_content();
 endwhile

 get_footer();
 ?>

function $funtionname$() {
$labels = array( 'name' => _x( '$Name$', 'Post Type General Name', '$themename$' ), 'singular_name' => _x( '$Name$', 'Post Type Singular Name', '$themename$' ), 'menu_name' => __( '$Name$', '$themename$' ), 'parent_item_colon' => __( 'Parent $Name$', '$themename$' ), 'all_items' => __( 'All $Name$', '$themename$' ), 'view_item' => __( 'View $Name$', '$themename$' ), 'add_new_item' => __( 'Add New $Name$', '$themename$' ), 'add_new' => __( 'Add New', '$themename$' ), 'edit_item' => __( 'Edit $Name$', '$themename$' ), 'update_item' => __( 'Update $Name$', '$themename$' ), 'search_items' => __( 'Search $Name$', '$themename$' ), 'not_found' => __( 'Not Found', '$themename$' ), 'not_found_in_trash' => __( 'Not found in Trash', '$themename$' ), );
$args = array( 'label' => __( '$Name$s', '$themename$' ), 'description' => __( '$Name$ news and reviews', '$themename$' ), 'labels' => $labels, 'supports' => array( 'title', 'editor', 'excerpt', 'author', 'thumbnail', 'comments', 'revisions', 'custom-fields', ), 'taxonomies' => array( 'genres' ), 'hierarchical' => false, 'public' => true, 'show_ui' => true, 'show_in_menu' => true, 'show_in_nav_menus' => true, 'show_in_admin_bar' => true, 'menu_position' => 5, 'can_export' => true, 'has_archive' => true, 'exclude_from_search' => false, 'publicly_queryable' => true, 'capability_type' => 'page', );
register_post_type( '$Name$', $args );
} add_action( 'init', '$funtionname$', 0 );

$args = array( 
    'post_type' => '$Name$', 
    'posts_per_page' => 10 
);
$loop = new WP_Query( $args );
while ( $loop->have_posts() ) : $loop->the_post();
    the_title();
    echo '<div class="entry-content">';
    the_content();
    echo '</div>';
endwhile;
